void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // 归一化坐标
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // 计算极坐标
    float r = length(uv);
    float a = atan(uv.y, uv.x);

    // 时间控制
    float t = iTime * 0.5;

    // 控制花瓣的数量
    float petals = 6.0;

    // 创建花瓣形状，基于sin函数
    float petalShape = 0.5 + 0.5 * sin(a * petals + sin(t * 2.0)) - r;

    // 使用smoothstep平滑边缘
    float flower = smoothstep(0.01, 0.02, petalShape);

    // 花朵展开动画
    float bloom = sin(t) * 0.5 + 0.5;
    flower *= bloom;

    // 花朵颜色
    vec3 color = mix(vec3(1.0, 0.8, 0.9), vec3(0.8, 0.1, 0.5), flower);

    // 背景颜色
    vec3 bgColor = vec3(0.1, 0.1, 0.2);

    // 最终颜色混合
    fragColor = vec4(mix(bgColor, color, flower), 1.0);
}
