float PI = 3.142;

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 center = vec2(iResolution.x/iResolution.y * 0.5, 0.5f);
    float radius = 0.5f;
    float maxSeconds = 1.0f;

    int tt = int(iTime)%int(maxSeconds);
    float t = float(tt);
    float seconds =  t + fract(iTime);
    vec2 Tex = fragCoord/ iResolution.y; 
	float dist = distance(Tex, center);

    if ( dist>0.49 && dist<0.5 ) 
    {
          fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
    else
    {
        if(-atan(center.x-Tex.x, center.y-Tex.y) > -( (seconds/maxSeconds) *2.0f*PI - PI) && ( dist <= radius))
        {

           float tail = -1.0+(seconds/maxSeconds)/dist;
           fragColor = vec4(tail, 1.0-tail, 0.3, 1.0);
        }
        else
        {
            fragColor = vec4(0.0f); 
        }
    }
}
