float NUM_TORUS = 10.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;
    vec3 col = vec3(0.0);

    for (float i = 0.0; i < NUM_TORUS; i ++)
    {
       float c = (i + 1.0) / NUM_TORUS;
       float v = sin(uv.x * 20.0 + iTime * 3.0 + i * 50.0) * 0.02;

       col += vec3(0.001 / abs(length(uv + v) - 0.4 * mix(1.0, c, 0.1))) * vec3(c, sin(iTime) * 0.5 + 0.5, 1.0 - c);
    }

    fragColor = vec4(col, 1.0);
}
