float rect(float step1, float step2, float x){
    return (x > step1 && x < step2) ? 1. : 0.;
}

vec3 rect(float s1, float s2, vec3 x){
    return vec3(rect(s1,s2,x.x),rect(s1,s2,x.y),rect(s1,s2,x.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //center uvs to 1:1 ratio
    uv.x = uv.x *iResolution.x/iResolution.y - (iResolution.x - iResolution.y)/(2.*iResolution.y);
    uv = uv - vec2(.5);

    //glitch by moving uv
    vec3 times = 0.02*sin(iTime * vec3(0.7,0.6,0.5) + (1.7,2.2,4.1)) + iTime * vec3(0.0551,0.047,0.06) + vec3(0.,21.871,1.273);
    vec2 tuv = vec2(uv.x * .41,uv.y*3.2);
    vec4 rrand = texture(iChannel0,vec3(tuv,times.x));
    vec4 grand = texture(iChannel0,vec3(tuv,times.y));
    vec4 brand = texture(iChannel0,vec3(tuv,times.z));

    if(mod(uv.x-0.45*iTime,6.) < 4.5){
        uv.y = uv.y+0.02 + (mod(uv.y,0.2) < 0.05 ? 0.02 : 0.);
        uv.x = uv.x+0.01;
    }

    // rgb uvs
    vec2 ruv = uv;
    vec2 guv = uv;
    vec2 buv = uv;
    if(mod(uv.x-0.45*iTime,6.) < 4.5){
        if(rrand.x > 0.97)
            ruv = uv + .5*rrand.yz*(rrand.w > 0.5 ? 1. : -1.);
        if(grand.w > 0.975)
            guv = uv + .5*grand.zx*(grand.y > 0.5 ? 1. : -1.);
        if(brand.z > 0.98)
            buv = uv + .5*brand.wy*(brand.x > 0.5 ? 1. : -1.);
    }

    //coloring
    vec3 col = vec3(rect(0.16,0.25,vec3(length(ruv),length(guv),length(buv))));
    float rangle = atan(-ruv.y,ruv.x);
    float gangle = atan(-guv.y,guv.x);
    float bangle = atan(-buv.y,buv.x);
    col = col -  col * step(0.95,sin(vec3(rangle,gangle,bangle)-0.25*iTime));

    // Output to screen
    fragColor = vec4(col,1.0);
}
