/*
More Raymarching stuff :)

Credit to this website for the raymarching tutorial:
https://inspirnathan.com/posts/47-shadertoy-tutorial-part-1
Credit to Inigo Quilez for his 3D Primitives.
*/
const float SPEED = 2.5;

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 50.0;
const float PRECISION = 0.001;
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}
mat3 identity() {
    return mat3(
        vec3(1, 0, 0),
        vec3(0, 1, 0),
        vec3(0, 0, 1)
    );
}

struct Surface {
    float sd;
    vec3 col;
};
Surface sdSphere(vec3 p, float r, vec3 offset, vec3 col){
  float d = length(p - offset) - r;
  return Surface(d, col);
}
Surface sdBoxFrame(vec3 p, vec3 b, float e, vec3 col){
  p = abs(p) - b;
  vec3 q = abs(p+e) - e;
  float d = min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
  return Surface(d, col);
}
Surface minWithColor(Surface obj1, Surface obj2) {
  if (obj2.sd < obj1.sd) return obj2; // The sd component of the struct holds the "signed distance" value
  return obj1;
}
Surface sdScene(vec3 p) {
  float newITime = iTime / SPEED;
  Surface sphere = sdSphere(vec3(sin(p.x / 2.), cos(p.y / 2.), sin(p.z / 2.)), 0.3, vec3(0.0), vec3(1, 0.992, 0.557));
  Surface boxFrame = sdBoxFrame(vec3(sin(p.x / 2.), cos(p.y / 2.), sin(p.z / 2.)), vec3(0.5), 0.07, vec3(0.));
  vec3 floorColor = vec3(0.5);
  Surface co = minWithColor(boxFrame, sphere);
  return co;
}
Surface rayMarch(vec3 ro, vec3 rd, float start, float end) {
  float depth = start;
  Surface co;

  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    co = sdScene(p);
    depth += co.sd;
    if (co.sd < PRECISION || depth > end) break;
  }

  co.sd = depth;

  return co;
}

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * sdScene(p + e.xyy).sd +
      e.yyx * sdScene(p + e.yyx).sd +
      e.yxy * sdScene(p + e.yxy).sd +
      e.xxx * sdScene(p + e.xxx).sd);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  vec3 col = vec3(0);
  vec3 ro = vec3(sin(iTime), 0., -iTime * 2.);
  vec3 rd = normalize(vec3(uv.x, uv.y - 0.5, -1));
  rd *= rotateX(sin(iTime) / 5. + 0.3);
  rd *= rotateY(sin(iTime) / 2.);
  rd *= rotateY(cos(iTime) / 2.);

  Surface co = rayMarch(ro, rd, MIN_DIST, MAX_DIST);

  if (co.sd > MAX_DIST) {
    col = vec3(0);
  } else {
    vec3 p = ro + rd * co.sd;
    vec3 normal = calcNormal(p);
    vec3 lightPosition = vec3(ro.x, 15.0, ro.z);
    vec3 lightDirection = normalize(lightPosition - p);

    float dif = clamp(dot(normal, lightDirection), 0.3, 1.);
      col = dif * co.col + vec3(0.835, 1, 1) * .2;
  }

  fragColor = vec4(col, 1.);
}
