void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float z = 0.2;

    float dist = length(uv);

    if (dist > 0.1) {
        float sinTime = sin(iTime * 5.0);
        float sinDist = sin(uv.x * uv.x + uv.y * uv.y);

        uv.y += sign(sin(uv.x * 100.0)) / 30.0 * sinDist * sinTime;
        uv.x += sign(cos(uv.y * 100.0)) / 30.0 * sinDist * sinTime;
    }

    float sinTime2 = sin(iTime * 2.0);
    float sinTime3 = sin(iTime * 2.0 + 3.0);

    uv.y += sign(sin(uv.x * 20.0)) / 200.0 * clamp(sinTime3, 0.0, 1.0);
    uv.x += sign(sin(uv.y * 20.0)) / 200.0 * clamp(-sinTime2, -1.0, 0.0);
    uv.x += sign(sin(uv.x * 20.0)) / 200.0 * clamp(-cos(iTime * 2.0), -1.0, 0.0);
    uv.y += sign(sin(uv.y * 20.0)) / 200.0 * clamp(sinTime3, 0.0, 1.0);

    float p = 0.2;
    float q = 0.229 - (1.0 + cos(iTime * 2.0 - exp(dist * 2.0))) / 45.0;
    float k = 6.0;
    float colc = mod(dist - iTime / k, p);
    float col1 = mod(length(uv + vec2(0.1, 0.0)) - iTime / k, p);
    float col2 = mod(length(uv + vec2(-0.1, 0.0)) - iTime / k, p);
    float col3 = mod(length(uv + vec2(0.0, -0.1)) - iTime / k, p);
    float col4 = mod(length(uv + vec2(0.0, 0.1)) - iTime / k, p);

    float colo = mix(col1, col2, 0.5);
    float coli = mix(col3, col4, 0.5);
    float col = mix(colo, coli, 0.5);
    float aa = (colo + coli) / 2.0;

    col = smoothstep(q, q - 0.15, col);
    float b = 1.0 - col;

    float ring = smoothstep(0.2, 0.1, 1.0 - smoothstep(1.0, 0.8, log(q * 10.59)) + q);

    fragColor = 1.5 * vec4(vec3(0.9, 0.2, 0.6) * vec3(b + aa, 0.1, aa + col * b * ring), 1.0);
}
