float bordercheck(vec2 uv) {
    return (floor(uv) == vec2(0.)) ? 0. : 1.;
}


mat2 r2d (float a) {
    float c, s;
    c = cos(a);
    s = sin(a);
    return mat2(c,s,-s,c);
}

#define _SPEED 17.
#define x_spacing round(1.)
#define y_spacing round(1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv *= r2d(radians(iMouse.x/iResolution.x*360. + iTime * _SPEED));
    uv *= 7.;
    uv +=.5;

    uv += 0.0;
    uv.x = mix(uv.x, mod(uv.x, x_spacing), 1.);//-iMouse.y/iResolution.y);
    uv.y = mix(uv.y, mod(uv.y, y_spacing), .3);
    uv -= 0.0;

    uv -= 0.5;
    uv *= 1.45;
    uv *= r2d(-radians(iMouse.x/iResolution.x*360. + iTime * _SPEED));
    uv += 0.5;

    // Time varying pixel color
    vec4 col = texture(iChannel0,uv);

    col.rgb = mix(col.rgb, vec3(0.), bordercheck(uv));

    // Output to screen
    fragColor = vec4(col);
}
