vec3 mini(vec2 uv,float scl)
{ 
    return vec3(uv.x*scl-float(int(uv.x*scl)),
                uv.y*scl-float(int(uv.y*scl)),
                ( float(int(uv.x*scl)) + float(int(uv.y*scl))*scl ) / (scl*scl-1.)  );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float scl = 10.;

    vec3 rgb = mini(vec2(uv.x*2.,uv.y),scl);
    vec3 hsv = hsv2rgb(mini(vec2(uv.x*2.,uv.y),scl).zxy);

    fragColor = uv.x<0.5 ?  vec4(rgb,1.0): vec4(hsv,1.0);

}
