vec2 cmul (vec2 a, vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.x * b.y + b.x * a.y);
}
vec2 cdiv (vec2 a, vec2 b) {
	float den = dot(b, b);
	return vec2(
		(a.x * b.x + a.y * b.y) / den,
		(a.x * b.x - a.y * b.y) / den
	);
}

