     Q = vec4(0.429,0.7,0.96,1);
     vec4 A = 3.*A(U).w*vec4(.5,.5,.5,1);
     vec4 B = 4.*B(U).w*vec4(.5,.0,.0,1);
     vec4 C = 4.*C(U).w*vec4(.0,.5,.0,1);
     vec4 D = 3.*D(U).w*vec4(.9,.9,.0,1);
     Q = 1.-2.*(1.-Q)*(1.-A-B-C-D);

     //Q += A+B+C+D;
}

vec2 R;
#define Main void mainImage(out vec4 Q, in vec2 U) {R = iResolution.xy;
#define A(u) texture(iChannel0,(u)/R)
#define B(u) texture(iChannel1,(u)/R)
#define C(u) texture(iChannel2,(u)/R)
#define D(u) texture(iChannel3,(u)/R)

Main 
     if ((iFrame)%2==0) {
         Q = A(U);
         vec2 V = unpackSnorm2x16(uint(Q.z));

         for (float x = -2.;x <= 2.;x++)
         for (float y = -2.;y <= 2.;y++) {
             vec4 q = A(Q.xy+vec2(x,y));
             vec4 b = B(Q.xy+vec2(x,y));
             vec2 v = unpackSnorm2x16(uint(q.z));
             vec2 r = normalize(q.xy-Q.xy);
             float l = length(r);
             if (l>0.) {
                 V += r/l/l*clamp(-.01*(q.w)-0.000*b.x,-1.,1.);
             }
         }
         //V -= .01*V;
         V *= .999;
         //V.xy *=(1.- 2.*(C(10.*U).w*2.-1.)*pow(C(10.*U).w,1000.)/length(V));

         V.xy -= .2*(C(U).xy*2.-1.)*pow(C(10.*U).z,1000.);
         Q.xy += .2*V;
        float ts = R.x/200.;

        float t = 0.;

        for(float i=.0;i<15.;i++)
        {

            //if (length(U-vec2(.5,.5)*R) < 50.) {

            //if(length(vec2(R.x,U.y)-U.xy)<15.){
            //float len = length(U - vec2(0.5*(1.0+sin(iTime+i*0.1)*(sin(iTime)))+(R.x-R.y)/R.x, 0.5*(1.0+cos(iTime+i*0.1)*(sin(iTime)))) * R.yy);
            float time = iTime - iTimeDelta*i;
            float len = length(U - vec2(0.5*(1.0+sin(time/ts+t*0.25))+2.*(R.x-R.y)/R.x, 0.5*(1.0+cos(time/ts+t*0.15))) * R.yy*abs(sin(time/ts)));
            if ( len<1.0) {
                Q = vec4(U,0,1);
                V = -.1*normalize(U-.5*R);
            }
        }

    if (U.x < 1.||R.x-U.x < 1.||R.y-U.y < 1.||U.y< 1.) Q.w *= 0.;

        Q.z = float(packSnorm2x16(V));
    } else {
        Q = vec4(0);
        vec2 V = vec2(0);
        for (float x = -4.;x <= 4.; x++)
        for (float y = -4.;y <= 4.; y++) {

            vec2 u = vec2(x,y);

            vec4 q = A(U+u);
            vec2 v = unpackSnorm2x16(uint(q.z));
            float k = 2.;
            //vec4 o = clamp(q.xyxy-U.xyxy+k*vec4(-.5,-.5,.5,.5),-.5,.5);
            vec2 o = q.xy-U.xy;
            vec2 o1 = clamp(o-1.,-.5,.5);
            vec2 o2 = clamp(o+1.,-.5,.5);
            //float w = (o.z-o.x)*(o.w-o.y)/k/k;
            float w = (o1.x-o2.x)*(o1.y-o2.y)/k/k;
            q.xy = U+0.5*(o1+o2);
            Q.xy += q.xy*q.w*w;
            V += v*q.w*w;
            Q.w += q.w*w;
        }
        if (Q.w>0.) {
            Q.xy/=Q.w;
            V /= Q.w;
        }
        Q.z = float(packSnorm2x16(V.xy));

    }


}

