float ditherOffset =0.00;
float contrast = 1.0;
float tileSize =1.;
float gamma = 1.;
float outLineThreshhold = 0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv1 = fragCoord/iResolution.xx;
    vec2 pixelSize = 1./iResolution.xy;

    vec4 ditherCol = texture(iChannel0,floor(fragCoord/tileSize)/8.+1./16.);

    vec4 imageCol = texture(iChannel1,floor(fragCoord/tileSize)*tileSize/iResolution.xy);
    float imageLuma = 0.299*imageCol.x +0.587*imageCol.y + 0.114*imageCol.z; 
    imageLuma = pow(imageLuma,gamma);
    imageLuma = (imageLuma-0.5)*contrast +0.5; 

    float value = 0.;
    if  (imageLuma > ditherCol.x + ditherOffset){
    value = 1.;
    }

    //outline
    vec4 xDiff = texture(iChannel1, uv + vec2(pixelSize.x,0.)) - texture(iChannel1, uv - vec2(pixelSize.x,0.));
    vec4 yDiff = texture(iChannel1, uv + vec2(0.,pixelSize.y)) - texture(iChannel1, uv - vec2(0.,pixelSize.y));
    float diff = length(xDiff) + length(yDiff);

    if (diff > outLineThreshhold){
    value = 0.;
    }

    fragColor = mix (vec4(0.165,0.384,0.604,0.), vec4(1.,0.855,0.47059,1.), value);


