void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy /iResolution.xy)*6.0;
    uv.x=mod(uv.x-iTime,6.0);
    // uv.x=uv.x+(tan(iTime))*3.0; // try sin and tan
    float r = 1.0,g=1.0,b=1.0,pi = 3.14159265359;
    if (uv.x<=1.0){
        r=1.0;
        b=0.0;
        g=sin(uv.x*pi/2.0);
    }
    else if (uv.x<=2.0){
        r=sin((2.0-uv.x)*pi/2.0);
        b=0.0;
        g=1.0;
    }
    else if (uv.x<=3.0){
        r=0.0;
        b=sin((uv.x-2.0)*pi/2.0);
        g=1.0;
    }
    else if (uv.x<=4.0){
        r=0.0;
        b=1.0;
        g=sin((4.0-uv.x)*pi/2.0);
    }
    else if (uv.x<=5.0){
        r=sin((uv.x-4.0)*pi/2.0);
        b=1.0;
        g=0.0;
    }
    else if (uv.x<=6.0){
        r=1.0;
        b=sin((6.0-uv.x)*pi/2.0);
        g=0.0;
    }

    vec3 col= vec3(r,g,b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
