const vec4 bgcol = vec4(217./255., 217./255., 253./255., 1.);
const vec4 col = vec4(0.439, 1., 0.086, 1.);

float radius = 100.;

const float scale = 5000.;

const float NR = 0.005*scale;
const float EW = 0.002*scale;

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 p = fragCoord-iResolution.xy/2.;
    float ds = dot(p, p);


    float srr = ds/((radius-EW)*(radius-EW));

    if(ds > radius*radius){
        fragColor = bgcol;
        return;
    }

    if(ds > NR*NR && srr < 1.){
        // Used the mix function because transparency won't work
        fragColor = mix(bgcol, col, .5);
    }else{
        fragColor = vec4(0.5*col.rgb, 1.);
    }
}
