#define CONSTANT_C 1.
#define CONSTANT_G 0.1

#define WORMHOLE_MASS 10.

bool marchlight(inout vec3 ro, inout vec3 rd, float mass, float c, float G, float cutoff_radius, int iterations) {
    bool through = false;
    for (int i=0; i<iterations; i++) {
        float r = length(ro);
        vec3 dir = -ro/r;

        float acceleration = (G*mass)/(r*r);

        float time = max(1./abs(acceleration) * .001,0.01);
        vec3 velocity = acceleration*dir*time;

        rd = normalize(rd+velocity);

        ro += rd*c*time;

        float schwarzschild = 2.*(G*mass)/(c*c);
        if ((r <= cutoff_radius && cutoff_radius > 0.0) || (r >= cutoff_radius && cutoff_radius < 0.0)) {
            through = true;
            break;
        } else if (r >= 10000.) {
            break;
        }
    }

    return through;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;
    vec2 m = (iMouse.xy-iResolution.xy/2.)/iResolution.y;

    vec3 camPos = vec3(0,0,cos(iTime*.05));
    camPos *= camPos*camPos;
    camPos *= 35.;

    vec3 ro = camPos;
    vec3 rd = getRayDir(vec3(0.,0.,1.),vec3(0),uv,45.);
    rd = rot3D(rd,-m.y*4.,-m.x*4.);

    vec3 col = vec3(0.);

    float schwarzschild = 2.*(CONSTANT_G*WORMHOLE_MASS)/(CONSTANT_C*CONSTANT_C);

    bool through = marchlight(ro, rd, WORMHOLE_MASS, CONSTANT_C, CONSTANT_G, schwarzschild*.01, 10000);
    if (!through) {
        col = texture(iChannel0, rd).rgb;
        if (camPos.z > 0.0) {
            col = texture(iChannel0, rd).rgb;
        } else {
            col = texture(iChannel1, rd).rgb;
        }
    } else {
        through = marchlight(ro, rd, -WORMHOLE_MASS, CONSTANT_C, CONSTANT_G, 0.0, 10000);
        if (camPos.z > 0.0) {
            col = texture(iChannel1, rd).rgb;
        } else {
            col = texture(iChannel0, rd).rgb;
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 getRayDir(vec3 ro, vec3 wo, vec2 uv, float FOV) {
    float t = tan(FOV*3.14159/180.);
    vec3 f = normalize(wo-ro),
        r = normalize(cross(vec3(0,1,0),f)),
        u = cross(f,r),
        rd = f+r*uv.x*t+u*uv.y*t;
    return normalize(rd);
}
mat2 rot2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(
        c,-s,
        s,c
    );
}
vec3 rot3D(vec3 p, float pitch, float yaw) {
    p.zy *= rot2D(pitch);
    p.xz *= rot2D(yaw);
    return p;
}

float intersectPlane(vec3 p0, vec3 n, vec3 ro, vec3 rd) {
    float denom = dot(n,rd);
    if (denom != 0.0) {
        vec3 p0ro = p0-ro;
        float t = dot(p0ro, n) / denom;
        return t;
    }

    return -1.0;
}
float intersectDisk(vec3 p0, vec3 n, float r, float R, vec3 ro, vec3 rd) {
    float t = intersectPlane(p0, n, ro, rd);
    if (t > 0.0) {
        vec3 p = ro + rd*t;
        float dr = length(p-p0);
        if (dr >= r && dr <= R) {
            return t;
        }
    }
    return -1.0;
}
