// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-06-27 03:11:17
#define S (2.-abs(p.x*sin(.2*p.z)+p.y*cos(.2*p.z)))
void mainImage(out vec4 O,vec2 F)
{
    vec3 p = vec3(0,0,iTime-30.), t = normalize(vec3(F-iResolution.xy*.5,30));
    for (float d;d<40.;d+=S)
    {
        p+=t*S;
        S<.001 ? d=40.
        : d;
    }
    O=2.-abs(mod(1.7*ceil(p.z+iTime)-vec4(0,2,4,1),6.)-3.);
}
