float alpha = 0.5; // this is a uniform

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 base = texture(iChannel0, uv);
    vec4 overlay = texture(iChannel1, uv);

    vec3 baseOverlayScaled = overlay.rgb * 2.0;
    vec3 baseScaled = base.rgb * 2.0;

    vec3 result1 = mix(
        vec3(1.0) - 2.0 * (vec3(1.0) - base.rgb) * (vec3(1.0) - baseOverlayScaled),
        2.0 * base.rgb * overlay.rgb,
        step(base.rgb, vec3(alpha))
    );

    vec3 result2 = mix(
        2.0 * base.rgb * overlay.rgb,
        vec3(1.0) - 2.0 * (vec3(1.0) - (baseScaled - vec3(1.0))) * (vec3(1.0) - overlay.rgb),
        step(overlay.rgb, vec3(alpha))
    );

    vec3 result = mix(result1, result2, step(base.rgb, vec3(alpha)));

    fragColor = vec4(result, base.a);
}
