float rect(vec2 st, vec2 size)
{
    size = 0.25 - 0.25 * size;

    vec2 uv = smoothstep(size, size + size * vec2(0.002), st * (1.0 - st));
    return uv.x * uv.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = fragCoord / iResolution.xy;
    vec3 influenced_color = vec3(0.577, 0.441, 0.700);

    vec3 influencing_color_A = vec3(0.319, 0.167, 0.365);
    vec3 influencing_color_B = vec3(0.628, 0.526, 0.775);

    vec3 color = mix(influencing_color_A, influencing_color_B, step(st.x, 0.5));

    fragColor = vec4(color, 1.0f);

    float min = -0.2;
    float max = 0.7;

    float offset = min + (max - min) * ((sin(0.4 * iTime) + 1.0) / 2.0);

    color = mix(color, influenced_color, rect(((st - vec2(offset, 0.0)) * vec2(2.0, 1.0)), vec2(0.05, 0.125)));
    fragColor = vec4(color, 1.0f);
}
