void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv1 = fragCoord/iResolution.xy;
    float scale = 10.0;
    float clarity = 100.0;
    vec2 shift1 = vec2(1, 1);
    //vec3 shift2 = vec3(4,5,4);
    vec3 shift2 = vec3(0,0,iTime);
    uv1 *= scale;
    uv1 += shift1;
    float uv1z = 10.0 * ((uv1.x - uv1.y) / (uv1.x + uv1.y));
    vec3 uv2 = vec3(2, 2, uv1z);

    vec3 col = clarity * cos(uv2 + shift2);


    // Output to screen
    fragColor = vec4(col,1.0);
}
