bool drawTriangle(vec2 fragCoord, vec2 v0, vec2 v1, vec2 v2, vec2 resolution) {
    vec2 v0Norm = vec2(v0.x, resolution.y - v0.y);
    vec2 v1Norm = vec2(v1.x, resolution.y - v1.y);
    vec2 v2Norm = vec2(v2.x, resolution.y - v2.y);

    vec2 e0 = v1Norm - v0Norm;
    vec2 e1 = v2Norm - v1Norm;
    vec2 e2 = v0Norm - v2Norm;

    vec2 c0 = fragCoord - v0Norm;
    vec2 c1 = fragCoord - v1Norm;
    vec2 c2 = fragCoord - v2Norm;

    bool inside0 = (e0.x * c0.y - e0.y * c0.x) <= 0.0;
    bool inside1 = (e1.x * c1.y - e1.y * c1.x) <= 0.0;
    bool inside2 = (e2.x * c2.y - e2.y * c2.x) <= 0.0;

    return (inside0 == inside1) && (inside1 == inside2);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 v0 = vec2(100.0, 300.0);
    vec2 v1 = vec2(300.0, 300.0);
    vec2 v2 = vec2(200.0, 100.0);

    if (drawTriangle(fragCoord, v0, v1, v2, iResolution.xy)) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
