// -2 chars  Thanks to GregRostami (2024-6-24)

void mainImage( out vec4 O, vec2 u)
{     
    u = 24.*( u+u - (O.xy=iResolution.xy)) /O.y;
    float a,r;                   
    for(int i; i++<182;O[i%4] = .5-.5*cos(a+a) )
         r = dot(u,u),
         r>1.?u*=r=1./r:u,        
         u *= mat2(cos(-1.685+vec4(0,33,11,0))) * -2.854,          
         //u *= mat2(cos(iTime*.01+vec4(0,33,11,0))) * -2.854,
         u.y += 1.62,            
         a=a*.99+r;       
}

