vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 draw(vec2 p, float start, float end, float iter,float t) 
{     
    if (p.x < start || p.x > end || p.y < start || p.y > end) {
        return vec3(0,0,0);
    }
    float len = end - start;
    float x = p.x - start;
    float y = p.y - start;
    vec3 a=vec3(0,1,1);
    for(float i=0.; i<iter; i++) {
        len /= 3.;         
        if (int(x/len) % 3 == 1 && int(y/len) % 3 == 1) {
            a+=vec3(1./(iter+1.),1,1);  
        }
        if (int(x/len) % 3 == 1 ) {
            a+=vec3(1./(iter+1.),1,1)/2.*(sin(t+length(p))+1.);  
        }
        if (int(y/len) % 3 == 1 ) {
            a+=vec3(1./(iter+1.),1,1)/2.*(sin(t+length(p))+1.);  
        }

    }
    return a;    
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{  
    vec2 p = (2.*fragCoord - iResolution.xy)/iResolution.y; // [-1,1] vertically    
    vec3 color = hsv2rgb(draw(p, -1., 1., 1.,iTime))-hsv2rgb(draw(p, -1., 1., 2.,iTime*1.1))+hsv2rgb(draw(p, -1., 1., 3.,iTime*1.2))-hsv2rgb(draw(p, -1., 1., 4.,iTime*1.3))+hsv2rgb(draw(p, -1., 1., 5.,iTime*1.4))-hsv2rgb(draw(p, -1., 1., 4.,iTime*1.3))+hsv2rgb(draw(p, -1., 1., 6.,iTime*1.5))-hsv2rgb(draw(p, -1., 1., 7.,iTime*1.6));

   fragColor = vec4(color,1);
}
