#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*3.;
    p.z-=3.;
    p=vec3(log(length(p)),p.z-tan(iTime/i),atan(p.x,p.y));
    p=abs(abs(R3D(i))-.07)-.05,
    d=min(min(p.z,p.x),p.y),
    e+=acos(d);    
}o+=vec4(sqrt(p),1)*3./i;
}
