bool pointInRect(vec4 rect , vec2 p){

    return rect.x <= p.x && rect.y <= p.y && rect.z >= p.x && rect.w >= p.y;
}

// center, radiusm point
bool pointInCircle(vec2 c, float r,  vec2 p){
   return distance(c, p) <= r;
}

vec4 getColorAtCircle(vec4 rect1, vec4 color1, vec3 circle2, vec4 color2, vec2 p){
    vec4 c = vec4(0, 0, 0, 0);

    if(pointInRect(rect1, p)){
        c = color1;
    }

    if(pointInCircle(circle2.xy, circle2.z, p)){
        c = color2;
    }

    if(pointInRect(rect1, p) && pointInCircle(circle2.xy, circle2.z, p)){
        c = mix(color1, color2, color2.w);
    }
    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 bgColor = vec4(1.0, 1.0, 1.0, 1);
    vec4 bg = vec4(0, 0, iResolution.x, iResolution.y);

    vec3 circle1 = vec3(200.0, 200.0, 200.0);
    vec3 circle2 = vec3(400.0, 400.0, 200.0);
    vec3 circle3 = vec3(iMouse.xy, 200.0);

    vec4 c1 = vec4(1.0, 0, 0, 1.0);
    vec4 c2 = vec4(0.0, 1.0, 0, 0.6);
    vec4 c3 = vec4(0.0, 0.0, 1.0, 0.6);

    vec4 c = bgColor;
    c = getColorAtCircle(bg, c, circle1, c1, fragCoord);
    c = getColorAtCircle(bg, c, circle2, c2, fragCoord);
    c = getColorAtCircle(bg, c, circle3, c3, fragCoord);
    fragColor = c;


}
