void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // centred fragcoord
    vec2 coord = fragCoord - iMouse.xy;
    vec3 col;
    if (length(coord)<25.0)
    {
    col = vec3(smoothstep(25.0,0.0,length(coord)));
    }
    else
    {
      col = vec3(0,0,0);
    }

    fragColor = vec4(col*iTime,0.0);
}
