vec3 bg_col = vec3(0.439,0.,0.878);
vec3 circle_col = vec3(0.314,0.094,0.851);
vec3 outline_col = vec3(0.243,0.224,0.847);

vec3 bg_col2 = vec3(0.502,0.188,0.82);
vec3 circle_col2 = vec3(0.314,0.255,0.627);
vec3 outline_col2 = vec3(0.369,0.125,0.49);

float grid_size = 0.25;
float outline_width = 0.01;
float circle_size = 0.125;

float circle(vec2 uv, float size, float grid_size)
{
    vec2 cuv = mod(uv + vec2(grid_size * 0.5), vec2(grid_size)) - vec2(grid_size * 0.5);
    return step(length(cuv), size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    vec2 uv1 = uv + vec2(sin(iTime * 3.0 + uv.y * 15.0) * 0.05, 0.0);
    vec2 uv2 = uv + vec2(sin(iTime * 3.0 + uv.y * 15.0 + 0.5) * 0.05, 0.0);


    float dist = circle(uv1, circle_size, grid_size);
    float outline = (1.0 - circle(uv1, circle_size - outline_width, grid_size)) * dist;
    outline += (1.0 - circle(uv1 + vec2(grid_size * 0.5), circle_size - outline_width, grid_size)) * circle(uv1 + vec2(grid_size * 0.5), circle_size, grid_size);
    outline = clamp(outline, 0.0, 1.0);

    float dist2 = circle(uv2, circle_size, grid_size);
    float outline2 = (1.0 - circle(uv2, circle_size - outline_width, grid_size)) * dist;
    outline2 += (1.0 - circle(uv2 + vec2(grid_size * 0.5), circle_size - outline_width, grid_size)) * circle(uv2 + vec2(grid_size * 0.5), circle_size, grid_size);
    outline2 = clamp(outline, 0.0, 1.0);

    // Output to screen
    vec3 col = mix(bg_col, circle_col, dist);
    col = mix(col, outline_col, outline);

    vec3 col2 = mix(bg_col2, circle_col2, dist2);
    col2 = mix(col2, outline_col2, outline2);

    fragColor = vec4(mix(col, col2, 0.5), 1.0);
}
