void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 tile = vec2(mod(fragCoord.x,100.0),mod(fragCoord.y,100.0));
    int tid =int(tile.x)+int(tile.y)*(int(iResolution.y)%100);
    // Time varying pixel color
    float rTime = iTime+0.0005*float(tid);
    float x_color = (rTime-floor(rTime));
    float y_color = (ceil(rTime)-rTime);
    float z_color = (rTime-floor(rTime)+0.5);
    // Output to screen
    fragColor = vec4(x_color,y_color,z_color,1.0);
}
