void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord.xy / iResolution.xy;


    float waveHeight = 0.1; 
    float waveSpeed = 5.0; // Скорость волны
    float frequency = 10.0; // Частота волны

    // Время анимации
    float time = iTime * waveSpeed;


    float wave = cos(sin(uv.x * frequency + time)) * waveHeight;


    float boatY = 0.3 + wave; // Высота лодки 

    // Рисуем воду
    vec3 waterColor = vec3(0.2, 0.7, 1.0); // Цвет воды
    fragColor = vec4(waterColor, 1.0);


    if (uv.y < boatY)
    {
        vec3 boatColor = vec3(0.0, 0.4, 1.0); 
        float boatWidth = 1.0; 
        if (uv.x > 0.5 - boatWidth && uv.x < 0.5 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }
     boatY = 0.2 + wave;

    if (uv.y < boatY)
    {
        vec3 boatColor = vec3(0.1, 0.5, 1.0); 
        float boatWidth = 1.0; 
        if (uv.x > 0.5 - boatWidth && uv.x < 0.5 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }





   float sunRadius = 0.1; // Радиус солнышка
    float sunX = 0.1; // Позиция по X
    float sunY = 0.9; // Позиция по Y


    if (distance(uv, vec2(sunX, sunY)) < sunRadius)
    {
        fragColor = vec4(1.0, 1.0, 0.0, 1.0); 
    }

    boatY = 0.5 ;

    if (uv.y < boatY && uv.y>  0.15 + wave)
    {
        vec3 boatColor = vec3(1.0, 1.0, 1.0); 
        float boatWidth = 0.1; 
        if (uv.x > 0.65 - boatWidth && uv.x < 0.55 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }

    if (uv.y < boatY && uv.y>  0.1 + wave)
    {
        vec3 boatColor = vec3(1.0, 1.0, 1.0); 
        float boatWidth = 0.1; 
        if (uv.x > 0.5 - boatWidth && uv.x < 0.4 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }
    if (uv.y+0.015 < boatY && uv.y>  0.1 + wave)
    {
        vec3 boatColor = vec3(0.4, 0.1, 0.0); 
        float boatWidth = 0.1; 
        if (uv.x > 0.545 - boatWidth && uv.x < 0.355 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }
    if (uv.y+0.015 < boatY && uv.y>  0.1 + wave)
    {
        vec3 boatColor = vec3(0.4, 0.1, 0.0); 
        float boatWidth = 0.1; 
        if (uv.x > 0.695 - boatWidth && uv.x < 0.505 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }




    uv.y = 1.0 - uv.y;
    float boatBaseWidth = 0.2; // Ширина лодки
    float boatHeight = 0.8; 
    float triangleHeight = 0.1; // Высота треуг





    if (uv.y < boatHeight + triangleHeight && uv.x > 0.5 && uv.x < 0.55 + boatBaseWidth &&uv.y > boatHeight + triangleHeight-0.15)
    {
        float triangleBase = boatBaseWidth; 
        float triangleY = (0.5 + boatBaseWidth - uv.x) * (triangleHeight / triangleBase);
        if (uv.y < boatHeight + triangleY)
        {
            fragColor = vec4(0.7, 0.3, 0.3, 1.0); 
        }
    }
    boatBaseWidth = 0.23;
    triangleHeight = 0.1;
    if (uv.y < boatHeight + triangleHeight && uv.x > 0.5 - boatBaseWidth && uv.x < 0.5 &&uv.y > boatHeight + triangleHeight-0.15 )
    {
        float triangleBase = boatBaseWidth; 
        float triangleY = (uv.x - (0.5 - boatBaseWidth)) * (triangleHeight / triangleBase);
        if (uv.y < boatHeight + triangleY)
        {
            fragColor = vec4(0.7, 0.3, 0.3, 1.0); 
        }
    }

    uv.y = 1.0 - uv.y;
    boatY = 0.127 + wave;

    if (uv.y < boatY)
    {
        vec3 boatColor = vec3(0.0, 0.3, 1.0); 
        float boatWidth = 1.0; 
        if (uv.x > 0.5 - boatWidth && uv.x < 0.5 + boatWidth)
        {
            fragColor = vec4(boatColor, 1.0); 
        }
    }
}
