// comment-out to see entire nested boxes structure
#define SHOW_ONLY_LEAF_BOXES


float hash11(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec3 hash31(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}


#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

// Tree navigation:
//     up = (index-1)>>1
//     left = (index<<1)+1
//     right = (index<<1)+2

//#define BOUNDING_BOX_LEVELS 5u
#define BOUNDING_BOX_LEVELS 6u
#define BOUNDING_BOX_NODE_COUNT ((1u << BOUNDING_BOX_LEVELS) - 1u)
#define BOUNDING_BOX_LEAF_BASE ((1u << (BOUNDING_BOX_LEVELS - 1u)) - 1u)
#define BOUNDING_BOX_LEAF_COUNT (1u << (BOUNDING_BOX_LEVELS - 1u))

struct bounding_box
{
    vec3 center;
    vec3 extent;
};

bounding_box bounding_boxes[BOUNDING_BOX_NODE_COUNT]; // ugly global variable in case array arguments are copied instead of being passed by reference.

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 500u

bounding_box enclosing_box(bounding_box a, bounding_box b)
{
    // assuming extent is positive
    vec3 v0 = min(a.center-a.extent,b.center-b.extent);
    vec3 v1 = max(a.center+a.extent,b.center+b.extent);
    return bounding_box((v1+v0)/2.0,(v1-v0)/2.0);
}

vec2 box_intersect(vec3 ro, vec3 rd, vec3 center, vec3 size)
{
    ro -= center;
    vec3 m = 1.0/rd; // can precompute if traversing a set of aligned boxes
    vec3 n = m*ro;   // can precompute if traversing a set of aligned boxes
    vec3 k = abs(m)*size;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return vec2(DIST_MAX); // no intersection
    return vec2( tN, tF );
}

vec2 box_intersect(vec3 ro, vec3 rd, bounding_box b)
{
    return box_intersect(ro, rd, b.center, b.extent);
}

bool box_inside(vec3 p, vec3 c, vec3 s)
{
    p -= c;
    p = abs(p)-s;
    return max(max(p.x,p.y),p.z) <= 0.0;
}

bool box_inside(vec3 p, bounding_box b)
{
    return box_inside(p, b.center, b.extent);
}

float frame(vec3 p, vec3 b, float e) // https://iquilezles.org/articles/distfunctions/
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float box(vec3 p, vec3 b) // https://iquilezles.org/articles/distfunctions/
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float box(vec3 p, vec3 c, vec3 e)
{
    return box(p-c,e);
}

float box(vec3 p, bounding_box b)
{
    //return box(p, b.center, b.extent-0.01/*HACK back-off from bounding box*/);
    return frame(p-b.center, b.extent-0.01/*HACK back-off from bounding box*/, 0.02);
}

float cylinder(vec3 p, float r, float h) // based on https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

vec4 intersect(vec4 a, vec4 b)
{
    return a.w > b.w ? a : b;
}

vec4 offset(vec4 c, float d)
{
    return vec4(c.xyz,c.w-d);
}

vec4 map_switch_internal(vec3 p, uint index)
{
/*
    switch(index)
    {
    }
*/
    return vec4(vec3(0), DIST_MAX);
}

vec4 map_specific(vec3 p, uint index)
{
    vec4 combined = vec4(vec3(0), DIST_MAX);

    // debug
    //if(iMouse.z > 0.0 || iTime < 15.0) // enable at beginning for preview image, later on mouse-click
#ifdef SHOW_ONLY_LEAF_BOXES
        if(index >= BOUNDING_BOX_LEAF_BASE) // show only leaf boxes
#endif
            if(length(bounding_boxes[index].extent) > 0.0)
                combined = combine(combined, vec4(0.5+0.5*bounding_boxes[index].center,box(p,bounding_box(bounding_boxes[index].center,bounding_boxes[index].extent-0.01)) - 0.01)); // NOTE: shrink box slightly to make sure it is inside bounding box

    return combine(combined, map_switch_internal(p, index));
}

vec4 map_generic(vec3 p)
{
    vec4 combined = vec4(vec3(0), DIST_MAX);

    uint index_stack[BOUNDING_BOX_NODE_COUNT]; // reserve size for worst case: entire tree
    uint stack_pointer = 0u;
    index_stack[stack_pointer++] = 0u; // start at root

    while(stack_pointer > 0u)
    {
        stack_pointer--;
        uint index = index_stack[stack_pointer];

        if(box_inside(p, bounding_boxes[index]))
        {
            combined = combine(combined, map_specific(p, index));

            if(index < BOUNDING_BOX_LEAF_BASE) // node is not a leaf
            {
                index_stack[stack_pointer++] = (index<<1u)+1u;
                index_stack[stack_pointer++] = (index<<1u)+2u;
            }
        }
    }

    return combined;
}

// based on https://iquilezles.org/articles/normalsSDF tetrahedron normals
