// adapted https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    vec3 ks[4] = vec3[4](k.xyy, k.yyx, k.yxy, k.xxx);
    vec3 s = vec3(0);
    for(int i = 0; i < 4; i++)
    {
        float d = DIST_MAX;

        uint index_stack[BOUNDING_BOX_NODE_COUNT]; // reserve size for worst case: entire tree
        uint stack_pointer = 0u;
        index_stack[stack_pointer++] = 0u; // start at root

        while(stack_pointer > 0u)
        {
            stack_pointer--;
            uint index = index_stack[stack_pointer];

            // NOTE: Determine inside which box(es) by position WITHOUT epsilon offsets...
            if(box_inside(p, bounding_boxes[index]))
            {
                // NOTE: ...but sample WITH epsilon offsets, so all samples of one normal are from the same maps
                d = min(d, map_specific(p + ks[i]*h, index).w);

                if(index < BOUNDING_BOX_LEAF_BASE) // node is not a leaf
                {
                    index_stack[stack_pointer++] = (index<<1u)+1u;
                    index_stack[stack_pointer++] = (index<<1u)+2u;
                }
            }
        }

        s += ks[i] * d;
    }

    return normalize(s);
}

vec4 trace(vec3 ro, vec3 rd, float t0, float t1)
{
    // NOTE: Limited number of iterations to avoid stalling
    //       when ray passes closely (just above EPSILON)
    //       in parallel to a surface.
    uint i = 0u;

    vec4 nearest = vec4(vec3(0), DIST_MAX);

    uint index_stack[BOUNDING_BOX_NODE_COUNT]; // reserve size for worst case: entire tree
    uint stack_pointer = 0u;
    index_stack[stack_pointer++] = 0u; // start at root

    // NOTE: currently UNSORTED traversal TODO: sort
    while(stack_pointer > 0u)
    {
        stack_pointer--;
        uint index = index_stack[stack_pointer];

        vec2 current_bounds = box_intersect(ro, rd, bounding_boxes[index]);
        //if(current_bounds.x < DIST_MAX)
        if(current_bounds.x < nearest.w) // ignore everything further away than current result
        {
            if(index < BOUNDING_BOX_LEAF_BASE) // node is not a leaf
            {
                index_stack[stack_pointer++] = (index<<1u)+1u;
                index_stack[stack_pointer++] = (index<<1u)+2u;
            }
#ifdef SHOW_ONLY_LEAF_BOXES
            else // leaf -> ray-march NOTE: remove this line to render interior nodes, too
#endif
            {
                vec4 h = vec4(0);
                float t;

                current_bounds = clamp(current_bounds,t0,t1);
                for(t = current_bounds.x; t < current_bounds.y && i < ITER_MAX; i++)
                //for(t = current_bounds.x+EPSILON; t < current_bounds.y && i < ITER_MAX; i++)
                {
                    h = map_specific(ro + rd * t, index);
                    if(h.w < EPSILON)
                    {
                        if(t < nearest.w) nearest = vec4(h.xyz,t);
                        break;
                    }
                    t += h.w;
                }
            }
        }
    }

    return nearest;
}
