void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;//中心移动到0.5，0.5的中心
    uv.x *= iResolution.x / iResolution.y;//屏幕的x,y轴要比例一致


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 circle_center = vec2(0.5,0.5);

    float A = 0.1;//振幅
    float F = 8.0;//频率
    float circle = 0.2 + A*cos( atan(uv.x,uv.y)*F + 30.0 * uv.x + 1.0);
    float tor = 1.5/iResolution.y;
    col*= smoothstep(circle,circle+tor, length(uv));

    // Output to screen
    fragColor = vec4(col,1.0);
}

