void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying factor
    float time = iTime * 0.5;

    // Apply a sine wave displacement along the x-axis

    float strength = sin(time * 4.0) * 0.08;

    float phase = 10.0 * sin( time * 3.8 );

    float displacement = sin(uv.y * 20.0 + phase + time) * strength;

    // Add the displacement to the u coordinate
    uv.x += displacement;

    // Sample the texture at the displaced UV coordinates
    vec4 texColor = texture(iChannel0, uv);

    // Output the color
    fragColor = texColor;
}
