const float greenLow = 0.4;
const float blueHigh = 0.2;
const float redHigh = 0.2;
float invLerp(float from, float to, float value){
  return (value - from) / (to - from);
}

float remap(float origFrom, float origTo, float targetFrom, float targetTo, float value){
  float rel = invLerp(origFrom, origTo, value);
  return mix(targetFrom, targetTo, rel);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 v = texture(iChannel1,uv);

    vec2 uvQuant = floor(uv * 100.0)/100.0;
    vec4 m = texture(iChannel0,uvQuant);        
    vec4 n = v;
    if ( uv.y < m.x ) n = vec4(1,0,0,1);


    v.a = float(v.g > greenLow && v.r < redHigh && v.b < blueHigh);

    fragColor = mix(v,n,v.a);
}
