vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

vec3 getBackgroundColor(vec2 uv) {
    uv += 0.5; // remap uv from <-0.5,0.5> to <0,1>
    vec3 gradientStartColor = vec3(1., 0., 1.);
    vec3 gradientEndColor = vec3(0., 1., 1.);
    return mix(gradientStartColor, gradientEndColor, uv.y); // gradient goes from bottom to top
}

float sdfEllipse(vec2 uv, float a, float b, vec2 offset) {
  vec2 rotated = rotate(vec2(uv.x,uv.y), cos(iTime)*0.5);
  return (((rotated.x-offset.x)*(rotated.x-offset.x))/(a*a))+((rotated.y-offset.y)*(rotated.y-offset.y)/(b*b)) - 1.;
}

vec3 drawScene(vec2 uv) {
  vec3 col = getBackgroundColor(uv);
  float ellipse = sdfEllipse(uv*(cos(iTime+1.)*0.3+1.), 0.1, 0.2, vec2(0., 0.05));
  float ellipse1 = sdfEllipse(uv*(cos(iTime+1.)*0.3+1.), 0.1, 0.1, vec2(0.1, -0.15));
  float ellipse2 = sdfEllipse(uv*(cos(iTime+1.)*0.3+1.), 0.1, 0.1, vec2(-0.1, -0.15));
  vec3 color=vec3(1., 0.94117647058, 0.54509803921);
  col = mix(color, col, step(0., ellipse));
  col = mix(color, col, step(0., ellipse1));
  col = mix(color, col, step(0., ellipse2));
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // <0, 1>
  uv -= 0.5; // <-0.5,0.5>
  uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

  vec3 col = drawScene(uv);

  // Output to screen
  fragColor = vec4(col,1.0);
}
