const float PI = 3.141592653589793238462643383279502884197169399375105820974944592307816406286208998628034825342117067982148086513282306647093844609550582231725359408128481;

float sdTriangleSharp(vec2 p, float r) {
    //r *= 2.6/4.5;
    //float a = PI/0.75 - PI/2.0; //2.6179938779914943653855361527329190701643078328125881841457
    //float d1 = dot(p, vec2(cos(-a), sin(-a)))+r;
    //float d2 = -dot(p, vec2(cos(a), sin(a)))+r;
    //float d3 = dot(p, vec2(0.0,1.0))+r;
    //return -min(d1,min(d2,d3));

    // by iq
    r *= 2.6/4.5;
    p.x = abs(p.x);
    p -= vec2(0.5,0.5*sqrt(3.0))*max(p.x+sqrt(3.0)*p.y,0.0);
    return -p.y - r;
}

// Main function
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    p *= 2.0;
    m *= 2.0;

	float d = sdTriangleSharp( p, 1.0 );

    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(60.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdTriangleSharp( m, 1.0 );
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.01, abs(length(p-m)-abs(d))-0.005));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.01, length(p-m)-0.03));
    }

	fragColor = vec4(col,1.0);
}
