float shape_d(vec2 p) {
    p.y += 0.125 * cos((beat - p.x) * pi);
    p.y += 0.250 * (fract(p.x * 4.0 - beat) * 2.0 - 1.0);
    p.x += 0.250 * (fract(p.y * 4.0 - beat) * 2.0 - 1.0);

    return length(p) - 0.5;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = uv * 2.0 - 1.0;
    p = p * iResolution.xy / iResolution.yy;

    vec3 col = vec3(0.0, 1.0, 0.0);

    col *= step(0.0, -shape_d(p));

    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv =  abs(uv - 0.5) -beat / 32.0;

    vec3 col = texture(iChannel0, uv).rgb;
    col = fract(col + vec3(0.125, 0.25, 0.375) * beat);

    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_fb = (uv - 0.5) / 1.10 + 0.5;
    vec2 uv_col = uv;
    uv_col.x = 1.0 - uv.x;

    vec3 mask = texture(iChannel0, uv).rgb;
    vec3 col = 1.0 - texture(iChannel1, uv_col).rgb;
    col = fract(col * 4.0 - beat  * 1.0);

    vec3 fb = fract(texture(iChannel2, uv_fb).rgb - 4.0 / 255.0);

    col = mix(col, fb, 1.0 - mask.g);

    fragColor = vec4(col,1.0);
}
