void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float D=32.;
	vec2 p=fragCoord-0.5*iResolution.xy;
	float r=max(sqrt(length(p)),1.);
	float t=-0.5*3.14/iResolution.x*r + 0.15*iTime;
	float c=cos(t),s=sin(t);t*=700.;
	r=30./r;
	float x=(c*p.x-s*p.y)*r+t,y=(s*p.x+c*p.y)*r+t;
	int i=int( (mod(x,D)>0.5*D)==(mod(y,D)>0.5*D) )*255;
	fragColor = vec4(0,i,255-i,1.0);
}
