// Fork of "Its Raining Jean Cloude!" by oneshade. https://shadertoy.com/view/3tVyW1
float speed = 0.2; 

void ImageLayer(inout vec3 rectangleColor, in vec2 p, in vec2 pos, in vec2 size, in int channel) 
{
    p = (p - pos) / size + 0.5;
    vec2 corner = abs(p - 0.5) - 0.5;
    float bbox = max(corner.x, corner.y);
    if (bbox < 0.) {
        if (channel == 1) {
            rectangleColor = texture(iChannel0, p).rgb;
        }else if(channel == 2) {
            rectangleColor = texture(iChannel1, p).rgb;
        }else if(channel == 3) {
            rectangleColor = texture(iChannel2, p).rgb;
        }else if(channel == 4) {
            rectangleColor = texture(iChannel3, p).rgb;
        }else if(channel == 5) {
            rectangleColor = texture(iChannel0, p).rgb;
        }else if(channel == 6) {
            rectangleColor = texture(iChannel1, p).rgb;
        }else if(channel == 7) {
            rectangleColor = texture(iChannel2, p).rgb;
        }
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 modUV = vec3(1.0);

    ImageLayer(modUV, uv, vec2( 0.0,  speed * iTime), vec2(1.72,0.96), 1);
    ImageLayer(modUV, uv, vec2( -0.44,  -0.98 +  speed * iTime), vec2(0.84,0.95), 2);
    ImageLayer(modUV, uv, vec2( 0.44,  -0.98 +  speed * iTime), vec2(0.84,0.95), 3);
    ImageLayer(modUV, uv, vec2( -0.36, -0.99 * 2.0 +0.25 + speed * iTime), vec2(1.02,0.485), 4);
    ImageLayer(modUV, uv, vec2( -0.36, -0.99 * 2.0 -0.25 + speed * iTime), vec2(1.02,0.485), 5);
    ImageLayer(modUV, uv, vec2( 0.52, -0.99 * 2.0 + + speed * iTime), vec2(0.71,0.98), 6);

    float distance = -0.99 * 3.0 + speed * iTime;
    if (distance >= 0.0)
    {
        distance = 0.0;
    }
    ImageLayer(modUV, uv, vec2( 0.0,  distance), vec2(1.75,0.98), 7);


    fragColor = vec4(modUV, 1.0);
}
