void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - iResolution.xy) / iResolution.y;

    vec2 a = cos(uv*cos(iTime*1.8+2.0)*(2.0+cos(iTime/10.0)*10.0))*sin(iTime/2.0);

    vec3 col = vec3(a.x-a.y, a.x*sin(iTime+1.5)*a.y, sin(tan(a.x+a.y))*(1.0-abs(uv.x*uv.y)));


    fragColor = vec4((fract(col*4.3)/fract(col*1.6)), 1.0);
}
