void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 1.5*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec3 ro = 3.0*normalize(vec3(sin(iTime), 0.3, cos(iTime)));
    vec3 ww = normalize(vec3(0.0,0.0,0.0) - ro);
    vec3 uu = normalize(cross(vec3(0.0,1.0,0.0), ww));
    vec3 vv = cross(ww,uu);
    vec3 rd = normalize(p.x*uu + p.y*vv + 1.5*ww);

    float t = 0.0;
    for(int i=0; i<27; i++)
    {
        vec3 p = ro + t*rd;
        float d = length(max(abs(p)-1.0, 0.0))-0.3;
        t += d;
        if(abs(d)<0.01) break;
    }

    vec3 col = vec3(0.0);
    if(abs(t) < 100.0)
    {
        vec3 p = ro + t*rd;
        vec3 n = 2.0*normalize(abs(p)-1.0)*sign(p);    
        col = 0.5 + 0.5*cos(n.yxz);
    }

    fragColor = vec4(col,1.0);
}
