#define PI 3.1415926

float sinFunc(vec2 uv,float dis) {
    float r = 0.;
    // abs(sin(t)) != sin(t) * 0.5 + 0.5
    return  r = sin(dis * 10. * PI + iTime * 5.) * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float dis = length(uv);
    float r = sinFunc(uv,dis);

    // Output to screen
    fragColor = vec4(r,.0,0.,1.0);
}
