/**/ // 107 chars
void mainImage( out vec4 C, vec2 U )
{
    C = iResolution.yyyy;
    U = fract(4.*U/C.y)-.5;
    C *= abs(U.x) - ( U.y > 0. ? .5 - U.y : .25 );
}/**/


