// IGM-like Pseudo-fractal. Mimics runaway cooling instability explosion at t=50sec.
// No documentation: short code demo by ukeshet for ICP course.
void mainImage(out vec4 O, vec2 C) {
    float j,S=10.,t=min(60.,iTime+2.5),u,v,l; mat2 m=mat2(cos(1.),sin(1.),-sin(1.),cos(1.));
    vec2 q,n=vec2(0),N=n,p,R=iResolution.xy,uv=C/R-.5;uv.y*=R.y/R.x;p=vec2(1.2,0)+2.*(exp(-.1*t))*uv;v=length(uv);
    for(j=0.;j++<40.;){p*=m;n*=m;q=p*S+j+n+t*(exp(-.05*t));n+=sin(q);N+=cos(q)/S;S*=1.7;} l=.01/length(N);u=(N.x+N.y+.6)*exp(-1.3*t*v*v);   
    O=vec4(vec3(1.+.2+exp(.1*t),.3+exp(.1*t),0)*pow(u+.5*l,3.1)+vec3(0,0,1)*pow(u+l,4.1),1.);
}
