# define randomLim(min, max, val) (val - min) / (max - min);
# define bg vec3(.176, .129, .211);

# define centeredCenter vec2(.5, .5);
# define bottomLeftCenter vec2(0., .1);
# define bottomRightCenter vec2(.7, 0.);
# define topCenter vec2(.2, 1);

# define centeredR .4;
# define bottomLeftR .2;
# define bottomRightR .4;
# define topR .1;


float random (vec2 uv) {
    return fract(sin(dot(uv.xy,
        vec2(12.9898,78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // fragCoord = (600, 300)
    // iResolution = (1280, 720)

    // x = 600 / 1280 = 0.47 
    // y = 300 / 720 = 0.42
    // uv = (0.47, 0.42)

    vec2 centerCenter = centeredCenter;
    vec2 centerBottom = bottomRightCenter;
    vec2 centerBottom2 = bottomLeftCenter;
    vec2 centerTop = topCenter;

    float rTop = topR;
    float rCenter = centeredR;
    float rBottom = bottomRightR;
    float rBottom2 = bottomLeftR;

    float distCenter = distance(uv, centerCenter);
    float distBottom = distance(uv, centerBottom);
    float distBottom2 = distance(uv, centerBottom2);
    float distTop = distance(uv, centerTop);

    // Базовый фон 
    vec3 col = bg;

    // Закрашенный фон 
    float BGGausMax = 0.9;
    float BGGausMin = 0.4;
    if (uv.y < BGGausMax) {
        vec3 colorLocal = vec3(.5, .0, .8);
        if (uv.y > BGGausMin) {
            float c = random(uv);
            float randomLimit = randomLim(BGGausMin, BGGausMax, uv.y);
            if (c > randomLimit) {
            col = colorLocal;
            }
        } else {
            col = colorLocal;
        }
    }

    // Верхний круг 
    if (distTop < rTop) {
        float distRel = distTop / rTop;
        vec3 colorLocal = vec3(.5, .0, .3);
        float gausStart = .1;
        if (distRel > gausStart) {
           float c = random(uv);
           float randomLimit = randomLim(1., gausStart, distRel);
           if (c < randomLimit) {
               col = colorLocal;
           }
        } else {
           col = colorLocal;
        }
    } 

    // Нижний круг слева
    if (distBottom2 < rBottom2) {
        float distRel = distBottom2 / rBottom2;
        vec3 colorLocal = vec3(.176, .129, .211);
        float gausStart = .1;
        if (distRel > gausStart) {
            float c = random(uv);
            float randomLimit = randomLim(1., gausStart, distRel);
            if (c < randomLimit) {
                col = colorLocal;
            }
        } else {
            col = colorLocal;
        }
    } 


    // Центральный круг 
    if (distCenter < rCenter) {
        float distRel = distCenter / rCenter;
        vec3 colorLocal = vec3(.5, .0, .3);
        float gausStart = .1;
        if (distRel > gausStart) {
            float c = random(uv);
            float randomLimit = randomLim(1., gausStart, distRel);
            if (c < randomLimit) {
                col = colorLocal;
            }
        } else {
            col = colorLocal;
        }
    } 

    // Нижний правый круг 
    if (distBottom < rBottom) {
        float distRel = distBottom / rBottom;
        vec3 colorLocal = vec3(.176, .129, .211);
        float gausStart = .1;
        if (distRel > gausStart) {
            float c = random(uv);
            float randomLimit = randomLim(1., gausStart, distRel);
            if (c < randomLimit) {
                col = colorLocal;
            }
        } else {
            col = colorLocal;
        }
    } 


    fragColor = vec4(col, 1.0);
}
