#define pi acos(-1.)

vec2 RotateUV(vec2 uv, float rotation) {
    return vec2(sin(rotation) * uv.y + cos(rotation) * uv.x, sin(rotation) * uv.x - cos(rotation) * uv.y); }

float Line(vec2 uv, vec2 a, vec2 b) {
	vec2 uva = uv - a, ba = b - a;
    float t = clamp(dot(uva, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a + ba * t;
    float d = length(c - uv);
	return min(1.0, 0.001/d); }

vec2 UVtoPolar(vec2 p)
{
    return vec2(atan(p.x, p.y), length(p));
}

vec2 PolarToUV(vec2 p)
{
    return vec2(sin(p.x), cos(p.x)) * p.y;
}


vec2 polarcords (float x, float p) {
    float u = fract(sin(x))*2.0*pi;
    return vec2(sin(u), cos(u)) * p; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;





    vec3 col = vec3(0.05, 0.05, 0.05);

    col = mix(col, vec3(0.1, 0.1, 0.1) + uv.y + uv.x, smoothstep(0.0, 1., uv.y ));



    float a = atan(uv.x, uv.y);





    for (int i = 0; i<=100; i++) {
        float rotation = pi * float(i)/float(100) * 2.;
        vec2 RotP2 = uv;
        RotP2 = RotateUV(uv, rotation);
        float dt = smoothstep(0.0, 1., Line(RotP2, vec2(0.0,0.1), vec2(0.0, 0.15)));

        col = mix(col, sin(a + iTime*2.+vec3(4, 2, 0))*0.5+0.5 , dt); }


