void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 warpedUv = uv;

    vec2 offset1 = vec2(0.01, 0.);
    vec2 offset2 = vec2(-0.01, 0.);
    vec2 offset3 = vec2(0.0, 0.);

    if(uv.y < 0.73 + 0.5*tan(iTime * 0.5) && uv.y >0.6 + 0.5*tan(iTime * 0.5)) {
        warpedUv = vec2((tan(uv.y * 100.)*0.15) * (1. - uv.y) + uv.x, uv.y);
    }

    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;

    vec3 col = vec3(r, g, b);
    fragColor = vec4(col,1.0);
}
