// First-Person variant of  https://shadertoy.com/view/XXSyz1

//#define rot(a)    mat2( cos(a+vec4(0,11,33,0)) )            // rotation                  
  #define rot(a)  mat2(cos(a),sin(a),-sin(a),cos(a))
#define hue(v)  ( .6 + .6 * cos( v  + vec4(0,23,21,0)  ) )    // hue

const float R0 = 20.  , R1 = 10.,   // S = 40 x 23 tiles      // large and small radii
            r  = R1/R0, ir = sqrt(1.-r*r);                    // antiderivative of 1/circonf(b)

#define conf(b) ( .996*  2./ir* atan( (r-1.)/ir* tan(b/2.) )  /* NB: tan(b/2) = ( -d + sqrt(d*d+q.y*q.y) )/q.y ) */ \
                ,b) // ,b to cancel

vec3 pos(vec2 I, float e ) {                                  // --- pos on torus
    I = ( I + vec2(.5,-.5) ) / S * 6.28;                      // torus angle coordinates
    float a = I.x, b = I.y, l;
    b = conf(b);  // not working: inverse needed              // conformal correction         
    l = R0 + e*R1*cos(b);
    return vec3( cos(a)*l , e*R1*sin(b) , sin(a)*l ); 
}

void mainImage(out vec4 O, vec2 u) {    
    float d, l, i, k = 2.,  // .9                             // tile multiplier
          t = T(51).z/.17;                                    // sub-time
    vec3  R = iResolution,  D, p, q, L,U;
                                                              // --- camera
    vec2  P = T(50).xy, 
          F = normalize( mix( dir(T(51).w), dir(T(50).z), t));// smooth angular direction
    p = pos( P +(t-2.)*F , 1.5 );                             // pos cam
    D = normalize( pos( P + (t+2.)*F , 1. ) - p );            // lookat     
    L = normalize( cross(pos(P,1.1)-pos(P,1.) , D ));         // frame: left, up vector
 // p = 90./R, 
 // D = normalize( pos( T(50).xy , 1. ) - p );                // lookat
 // L = normalize(cross(D,vec3(0,1,0)));                      // frame: left, up vector
    U = cross(D,L);
    u = ( u+u - R.xy ) / R.y;
    D = normalize( u.x*L + u.y*U + 1.*D ); // 4.              // ray direction

    O *= 0.; l-=l, t=9.;
    if ( T(50).w < 0. ) O = hue(30.*iTime);
    for ( ; i++ < 1e2 ; ) {                                   // --- ray-marching
        q = p,
        l += t = abs( length(vec2(d=length(q.xz)-R0,q.y)) - R1); // abs for inside + outside

        float a = atan(q.z,q.x), b = atan(q.y,d);             // base torus parameterization
        b = conf(b);                                          // conformal correction
        q = vec3( k*R0*a/6.283, round(k*R1*b/6.283), 0 );     // cell id
        a = sin(k*R0*a/2.), b = cos(k*R1*b/2.);
        d = sqrt( 1. - min(abs(a)*R.y/80.,1.) * min( abs(b)*R.y/80.,1.) )  / exp( (l-80.)/30. ); 
     // d = sqrt( 1. - min(abs(a)/fwidth(a),1.) * min( abs(b)/fwidth(b),1.) ) / exp( (l-80.)/30. ); // color: mesh * depth darkening
        if ( t < .01 ) 
          { O += d;                                           // draw mesh
            d = T(mod(q.xy,S)).x;                             // cell content
            if ( d > 0. ) O.g ++;
            if ( d < 0. ) O.r ++;
            t = .2;                                           // traverse the layer and continue
          }
        p += t*D;                                             // step forward = dist to obj          
    }
 // u = round(R.y*u/2.); if (u.x*u.y==0.) O.r++;              // debug: show center
}

#define key(a) (texelFetch(iChannel3,ivec2(a,1),0).x > 0.)      // LEFT:37  UP:38  RIGHT:39  DOWN:40 
#define H(t)    fract(sin(mod( (t) *vec2(12.9898, 78.233 ), 6.28)) * 3758.5453)

vec2 new() {                                                    // --- create a new pills
    vec2 P;
    for ( float i; i++ < 50.; ) {
        P = floor( S* H(iTime - i*17.971) );
        if ( T(P.xy).x == 0. && P != T(50).xy ) return P;       // ok if free cell
    }
    return vec2(-1);
}

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    O = T(U);                                                   // restore state
    if (iFrame < 1 || T(50).w <  1. ) {                         // --- init
        if  ( T(50).w < 0. ) {                                  // wait 2" after loosing
            if ( U == vec2(50) )  O.w += 1.;
            return;
        }
        O *= 0.;
        if ( U == vec2(50) )                                    // init snake pos,dir,length
            O = vec4( floor(S*H(iTime)),  floor(4.*H(iTime+.5).x), 5 );
        if ( U == new() ) O.x = -1.;                            // set a pills
        return;
    }
    float c;
    if ( U == vec2(51) ) c = key(37) ? 1. : key(39) ? 0. : -1.,  // push events key events during pause
                         c >= 0. ? O.y++, O.x = float(int(c + 2.*O.x) & 0xfff) : c, // 0.x bits = FIFO, 0.y = next slot
                         O.z += iTimeDelta;                     // to control the pause time
    if (T(51).z < .17 ) return;                                 // slow game
    if ( U == vec2(50) ) {                                      // --- snake control
        c = T(51).y; if (c > 0.) c = ((int(T(51))>>int(c-1.))&1) > 0 ? 1. : -1.; // c = pop bufferized action
        O.z += c;                                               // user changes direction
        O.xy = mod( O.xy + dir(O.z) , S );                      // pos += dir
        if ( T(O.xy).x < 0. ) O.w++;                            // eat pills
        if ( T(O.xy).x > 0. ) O.w = -120.;                      // cross itself: loose !
    }
    else if ( U == vec2(51) ) 
        O.w = T(50).z,                                          // previous direction
        O.y = max(0.,O.y-1.), O.z = 0.;                         // event eaten, reset delay timer
    else if( U.x < S.x && U.y < S.y ) {                         // --- grid management
        vec4 P = T(50);                                         // snake state
        if (O.x > 0.) O.x--;                                    // snake trail
        if ( U==P.xy ) O.x = P.w;                               // snake head: timer = length
        if ( T(P.xy).x < 0. && U==new() ) O.x = -1.;            // pills eaten: new one            
    }
}
