//Buffer A is where it is at
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy/iResolution.xy;

    //distance the ray travelled was saved in alpha channel
    float maxDist = texture(iChannel0,uv).a;

    vec2 texel = 1./iResolution.xy;
    vec4 total_color;
    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }

    fragColor = .2e2*total_color*exp(-maxDist*maxDist/4.);

    fragColor = .7*fragColor*fragColor*fragColor + iResolution.y/50.*fwidth(fragColor*fragColor/iResolution.y*600.);


}

vec3 ifs_color, ro, tc;
float ifs_scale = 1.4, maxDist;

mat2 rot(float a) {return mat2(cos(a),-sin(a),sin(a),cos(a));}

float de(vec3 z) {

    vec3 orig_z = z;

    z.xy *= rot(iTime/8.);
    z.yz *= rot(6.17/10.); 
    z.xz *=  rot(5.5/7. + .2*sin(iTime/5.) ); 
    vec3 min_vtx;

    int n=0;
    float min_dist,dist_to_vtx;
    ifs_color = vec3(0.);
    for (int i=0; i<5; i++) { 
        min_vtx = d14[0];
        min_dist=length(z-d14[0]);
        for (int j=1; j<24; j++) {    
            dist_to_vtx=length(z-d14[j]); 
            if (dist_to_vtx<min_dist) {min_vtx=d14[j]; min_dist=dist_to_vtx;}   
        }
        z = min_vtx + ifs_scale*(z-min_vtx);    
        n++;
    }

    float lz = length(z);
    float dz = pow(ifs_scale, float(n) );

    vec3 cc = cos(z/dz);

    ifs_color = cc; 
    return lz / dz; 

}

float march(vec3 ro, vec3 rd) {
    float t = 0.0,d;
    for(float i = 0.; i < 4.; i++) {
        d = de(ro + rd*t); 
        t += .7*d; 
        tc += ifs_color*ifs_color*exp(-i*i*t*t*3e-2);
    }  
    return t;
}

vec3 render(vec3 ro, vec3 rd) {
    float t = march(ro, rd);
    maxDist = t;   
    return tc;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (2.*fragCoord.xy-iResolution.xy)/iResolution.y*.5;
    ro = vec3(0,0,-2.9); 
    vec3 rd = normalize( vec3(p,1.4) );

    vec3 col = render(ro, rd);
    col = 1. - exp(-col);
    fragColor = vec4( pow(col,vec3(32.)), maxDist);


}
