#define h21(a) fract(1e3 * sin(dot(a, r)))
#define pal(a) (.8 + h21(a+.1) / 2.6 * cos(2.* pi * h21(a) + vec4(0, 23, 21, 0)))
#define pi acos(-1.)

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
         u = (u + u - r) / r.y;

    u = vec2(
            log(dot(u, u)),
            atan(u.x, u.y) * 6. / pi 
        ) + iTime * .3;

    vec2 id = floor(u); // 

    u += sin(
             tanh(
                 cos(iTime) * 3. - 2.
             ) * 5. + u.yx * pi
         ) * .1;

    o.zw = mod(floor(u), 12.); // id, pos deform

    o.xy = fract(u) - .5 ;
             + cos(
                   h21(id + 1.) * 2.
                   + vec2(0, 11)
               ) * .5;

    vec2 f = mod(
                 floor(
                     cos(
                         o.xy
                         + h21(id + 2.) * iTime
                     ) * 22.
                 ), 
                 12.
             );

    float g = length(
                  tanh(
                      cos(
                          atan(o.x, o.y) * 4. 
                          + length(
                                id * iTime * .01
                            ) * pi
                      ) * 4.
                  )
              );

    o.x = .54 - length(fract(u) - .5);

    if(cos(iTime) > .1 * o.w)
    o.x = max(o.x, .0);

    o.x = abs(o.x);

    //if(false)
    o = pow(
            o.x //* g * 1.1
            + pow(o.x + .43, 30.) * 2.,
            .4
        ) * pal(f);
}
