vec4 gradient( vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    return vec4(col,1.0);
}



bool isEven (int num) {
    return mod(float(num), 2.) == 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1, 1, 1, 1);
    float x = (120.) * iTime;
    float diff = length(iResolution.xy / vec2(1280., 720.));
    int w = int((fragCoord.x + x) / (75. * diff));
    int h = int((fragCoord.y + x) / (75. * diff));
    if((isEven(w) == true && isEven(h) == true) || isEven(w) == false && isEven(h) == false) {
    fragColor = gradient(fragCoord);

    }

}
