renderpass.code:
vec3 linear2sRGB(vec3 rgb) {return mix(12.92 * rgb, 1.055 * pow(rgb, vec3(1.0 / 2.4)) - 0.055, step(vec3(0.0031308), rgb));} //Thank you ockiller! https://www.shadertoy.com/view/43dGDl


vec3 palette(float t) //palette function by iq https://iquilezles.org/articles/palettes/
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.00, 0.33, 0.67);
    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 tri(vec2 t){
    return 1.-abs(mod(t,2.)-1.);
}

vec2 phi(vec2 uv,float a,float b){
    uv-=0.5;
    uv = vec2(pow(length(uv)*2.,a),2.*atan(uv.y,uv.x)/6.28);
    uv=tri(3.*uv-b*iTime);

    uv-=0.5;
    uv = vec2(pow(length(uv)*2.,a),2.*atan(uv.y,uv.x)/6.28);
    uv=tri(2.*uv+b*iTime);
    return uv;
}


vec4 render(vec2 coords){
    float ar = iResolution.x/iResolution.y;
    vec2 uv = coords / iResolution.y;
    uv.x-=(ar-1.0)*0.5;

    vec2 uv1 = phi(uv,0.5,0.2);
    vec2 uv2 = phi(uv,0.6,0.21);



    float mask1 = smoothstep(0.01,-0.01,length(uv1-0.5)-0.35);
    float mask2 = smoothstep(0.01,-0.01,length(uv2-0.5)-0.42);
    vec3 col = mix(linear2sRGB(vec3(0,0,0)),linear2sRGB(vec3(0.2+0.5*uv2.x,0,0)),mask1);

    float outline1 = smoothstep(0.01,-0.01,abs(length(uv1-0.5)-0.35)-0.02);
    col = mix(col,linear2sRGB(vec3(0.07,0,0)),outline1);

    col = mix(col,palette(uv2.x*0.3+iTime*0.1+uv.y*0.3),mask2);


    float outline2 = smoothstep(0.01,-0.01,abs(length(uv2-0.5)-0.43)-0.02);
    col = mix(col,vec3(0,0,0),outline2);

    vec3 c = vec3(col);
    return vec4(c,1);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = render(fragCoord);  
}
